func convertBytesToChars(input []byte) (string, error) {
	chByte := byte(0)
	output := ""
	for i := 0; i < len(input); i++ {
		bit, err := strconv.Atoi(string(input[i]))
		if err != nil {
			return "", err
		}

		if i%8 == 0 && i != 0 {
			output += string(chByte)
			chByte = 0
		}

		chByte <<= 1
		chByte |= byte(bit)
		//fmt.Printf("%b\n", chByte)
	}

	return output, nil
}
